<script setup>
    import FloatingConfigurator from '@/components/FloatingConfigurator.vue';
    import { ref } from 'vue';

    import axios from 'axios';
    import { useRouter } from 'vue-router';

    // Importar primero los módulos de PrimeVue
    import { useToast } from 'primevue/usetoast';

    // Importar otros componentes y composables
    // import AppConfig from '@/layout/AppConfig.vue';
    import { useLayout } from '@/layout/composables/layout';

    // Declaración de variables reactivas
    const email = ref('');
    const password = ref('');
    const display = ref(false);

    // Inicialización de router y toast
    const router = useRouter();
    const toast = useToast();

    // Obtener configuración de layout y URL del logo
    const { layoutConfig } = useLayout();

    // Función para mostrar el toast
    const mostrarToast = (severity, summary, detail) => {
        console.log(severity + summary + detail);
        toast.add({ severity, summary, detail, life: 3000 });
    };

    // Función para asegurar el dominio del correo
    const asegurarDominioCorreo = (correo) => {
        // Verificar si el correo contiene un '@'
        if (!correo.includes('@')) {
            // Si no contiene '@', agregar el dominio por defecto
            return `${correo}@equinsa.es`;
        } else {
            // Obtener el dominio del correo
            const dominio = correo.split('@')[1];
            // Verificar si el dominio no es 'equinsa.es', cambiarlo por 'equinsa.es'
            if (dominio !== 'equinsa.es') {
                const nombreUsuario = correo.split('@')[0];
                return `${nombreUsuario}@equinsa.es`;
            }
        }
        // Devolver el correo original si ya es válido
        return correo;
    };

    // Función para manejar el inicio de sesión
    const handleLogin = () => {
        if (!email.value) {
            mostrarToast(
                'error',
                'Error iniciando sesión',
                'Por favor, ingresa correo electrónico'
            );
            return;
        }
        if (!password.value) {
            mostrarToast(
                'error',
                'Error iniciando sesión',
                'Por favor, ingresa contraseña'
            );
            return;
        }

        axios
            .post('/api/public/login', {
                email: asegurarDominioCorreo(email.value),
                pass: password.value
            })
            .then((response) => {
                console.log(response);
                if (response.data.datos.token) {                    
                    localStorage.setItem('token', response.data.datos.token);
                    router.push('/pages/dashboard');
                } else {
                    mostrarToast(
                        'error',
                        'Error',
                        'Los datos introducidos no son correctos'
                    );
                    console.log(response.data);
                    console.error('Error al hacer login');
                }
            })
            .catch((error) => {
                console.error('Error al iniciar sesión:', error);
                mostrarToast('error', 'Error', 'Error al iniciar sesión');
            });
    };

    const open = () => {
        display.value = true;
    };

    const close = () => {
        display.value = false;
    };

    const enviarCorreoRecuperacion = () => {
        // Expresión regular para validar el formato del correo electrónico
        const emailRegex = /^[^\s@]+@[^\s@]+\.[^\s@]+$/;

        if (!email.value || !emailRegex.test(email.value)) {
            mostrarToast(
                'error',
                'Error',
                'Por favor, ingresa un correo electrónico válido'
            );
            return;
        }

        axios
            .post(
                '/cbtback/enviarEnlaceRecuperacion',
                {
                    email: asegurarDominioCorreo(email.value)
                }
            )
            .then((response) => {
                mostrarToast(
                    'success',
                    'Correo enviado',
                    `Se ha enviado un correo a ${asegurarDominioCorreo(email.value)} con instrucciones para recuperar tu contraseña`
                );
                display.value = false;
            })
            .catch((error) => {
                console.error('Error al enviar correo de recuperación:', error);
                mostrarToast(
                    'error',
                    'Error',
                    'Error al enviar correo de recuperación'
                );
            });
    };
</script>

